import { useState } from 'react';

// éviter de render tjrs les headers, les stocker au lieu de Object.keys(data[0]).map((column) dans le html ?

const Table = ({ data }) => {
  const [tableHeaders, setTableHeaders] = useState(Object.keys(data[0]))
  const [sortOrder, setSortOrder] = useState({});
  const [sortedData, setSortedData] = useState(data);
  const [searchWord, setSearchWord] = useState('');
  const [pageNumber, setPageNumber] = useState(1);
  const [totalPages, setTotalPages] = useState();
  const [totalNumberOfRows, setTotalNumberOfRows] = useState()
  const [numberOfRowsDisplayed, setNumberOfRowsDisplayed] = useState(2);

  const handleSort = (column) => {
    const sortDirection = sortOrder[column] === 'asc' ? 'desc' : 'asc';
    const sorted = [...sortedData].sort((a, b) => {
      if (a[column] < b[column]) {
        return sortDirection === 'asc' ? -1 : 1;
      }
      if (a[column] > b[column]) {
        return sortDirection === 'asc' ? 1 : -1;
      }
      return 0;
    });
    setSortOrder({ [column]: sortDirection });
    setSortedData(sorted);
  };

  const handleSearch = (event) => {
    const searchedWord = event.target.value;
    setSearchWord(searchedWord);
    const filtered = data.filter((item) =>
      Object.values(item).some((value) =>
        value.toString().toLowerCase().includes(searchedWord.toLowerCase())
      )
    );
    setSortedData(filtered);
  };

  // const handleNumberOfRowsDisplayed = (event) => {
  // }

  return (
    <div>
      {/* <div>
        <p>Show</p>
        <select value={numberOfRowsDisplayed} onChange={handleNumberOfRowsDisplayed}>
          <option value="10">10</option>
          <option value="20">20</option>
          <option value="50">50</option>
        </select>
        <p>entries</p>
      </div> */}
      <input type="text" placeholder="Search" value={searchWord} onChange={handleSearch} />
      <table>
        <thead>
          <tr>
            {tableHeaders.map((column) => (
              <th key={column} onClick={() => handleSort(column)}>
                {column}
                {sortOrder[column] === 'asc' ? <span>&#9650;</span> : <span>&#9660;</span>}
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {sortedData.map((item, index) => (
            <tr key={index}>
              {Object.keys(item).map((key) => (
                <td key={key}>{item[key]}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
      <div>Showing {pageNumber} to {totalPages} of {totalNumberOfRows} entries</div>
    </div>
  );
};

export default Table;
